using System;using System.IO;namespace AlgorithmPrograms{    class Program    {       public static void Main()       {            string filepath = @"C:\Users\Sona G\source\repos\AlgorithmPrograms\AlgorithmPrograms\wordFile.txt";            string text = File.ReadAllText(filepath);            string[] textArray = text.Split(" ");            Console.WriteLine("1.Permutation of string\n2.Binary Search\n3.Insertion Sort\n4.Bubble Sort\n5Merge Sort\n6.Anagram\n7.Prime Range\n8.Guess Number");            Console.Write("Enter your choice:");            int choice = Convert.ToInt32(Console.ReadLine());            switch (choice)            {                case 1:                    String str = "ABC";                    int n = str.Length;                    Permutation.Permute(str, 0, n - 1);                              break;                case 2:                                        BinarySearch<string> binarySearch = new BinarySearch<string>(textArray);                    Console.WriteLine("Enter a word to search:");                    string word = Console.ReadLine();                    int found = binarySearch.FindWord(word, 0, textArray.Length - 1);                    if (found == 1)                    {                        Console.WriteLine("The word {0} is found", word);                    }                    else                    {                        Console.WriteLine("the word {0} is not found", word);                    }                    break;                case 3:                                        InsertionSort<string> insertionSort = new InsertionSort<string>();                    insertionSort.Sort(textArray);                    insertionSort.Display(textArray);                    break;                case 4:                    int[] arr = { 77,7,17,34,56,12,68 };                    BubbleSort<int> bubbleSort = new BubbleSort<int>();                    arr = bubbleSort.Sort(arr);                    bubbleSort.Display(arr);                    break;                case 5:
                    MergeSort merge = new MergeSort();
                    merge.Sort(textArray, 0, textArray.Length - 1);
                    Console.WriteLine("\nSorted array");
                    merge.PrintArray(textArray);                    break;                case 6:                    Anagram anagram = new Anagram();                    anagram.CheckAnagram("abcd", "dcba");                    break;
                case 7:                    PrimeNumberRange prime = new PrimeNumberRange();                    prime.IsPrime(1,100);
                    prime.PrintPalindrome();                    break;                case 8:                    Console.WriteLine("choose a number between 1-100");
                    GuessNumber magic = new GuessNumber();
                    magic.Guess();                    break;                default:                    Console.WriteLine("Exit");                    break;            }            Console.Read();       }    }        }